package com.example.studentappimport android.content.Contextimport android.net.ConnectivityManagerimport android.net.NetworkCapabilitiesimport android.util.Logimport com.example.studentapp.database.MaterialDatabaseimport com.example.studentapp.database.MaterialsDataimport com.example.studentapp.questions.Questionsimport com.google.firebase.auth.ktx.authimport com.google.firebase.firestore.ktx.firestoreimport com.google.firebase.ktx.Firebaseimport kotlinx.coroutines.CoroutineScopeimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launchobject GeneralFunctions {    fun checkForInternet(context: Context): Boolean {        val connectivityManager =            context.getSystemService(Context.CONNECTIVITY_SERVICE) as ConnectivityManager        val network = connectivityManager.activeNetwork ?: return false        val activeNetwork = connectivityManager.getNetworkCapabilities(network) ?: return false        return when {            activeNetwork.hasTransport(NetworkCapabilities.TRANSPORT_WIFI) -> true            activeNetwork.hasTransport(NetworkCapabilities.TRANSPORT_CELLULAR) -> true            else -> false        }    }// fun checkForInternet(context: Context): Boolean    var check = false    var check1 = false}