package com.example.studentapp.adapterimport android.content.Contextimport android.content.Intentimport android.net.Uriimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.ImageViewimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.example.studentapp.Rimport com.example.studentapp.database.MaterialsDataclass MaterialsAdapter(    val context: Context,    private val materialsList: MutableList<MaterialsData>,) : RecyclerView.Adapter<MaterialsAdapter.MaterialsViewHolder>() {    override fun onCreateViewHolder(        parent: ViewGroup,        viewType: Int    ): MaterialsAdapter.MaterialsViewHolder {        val view = LayoutInflater.from(context).inflate(R.layout.material_item, parent, false)        return MaterialsViewHolder(view)    }    override fun onBindViewHolder(holder: MaterialsAdapter.MaterialsViewHolder, position: Int) {        val allMaterialsModel = materialsList[position]        holder.bind(allMaterialsModel)    }    override fun getItemCount(): Int = materialsList.size    inner class MaterialsViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        fun bind(materialModel: MaterialsData) {            val img = itemView.findViewById<ImageView>(R.id.materialImage)            val title = itemView.findViewById<TextView>(R.id.materialTitile)            val desc = itemView.findViewById<TextView>(R.id.materialDesc)            val openLink = itemView.findViewById<TextView>(R.id.openLink)            openLink.setOnClickListener {                val url = materialModel.materialURL                val intent = Intent(Intent.ACTION_VIEW, Uri.parse(url)).also { (it) }                intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK)                context.startActivity(intent)            }            title.text = materialModel.materialTitle            desc.text = "${materialModel.materialDesc}"            Glide.with(context)                .load(materialModel.materialImgURI)                .placeholder(R.drawable.defoultimage)                .into(img)        }//bind    }}