package com.example.studentapp.adapterimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.ImageViewimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.example.studentapp.Rimport com.example.studentapp.models.QuestionsTypeModelimport com.example.studentapp.questions.Questionsclass QuizAdapter(    private val questionsList: MutableList<QuestionsTypeModel>,    private val onItemClick: (questionsTypeModel: QuestionsTypeModel) -> Unit) : RecyclerView.Adapter<QuizAdapter.ViewHolder>() {    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder = ViewHolder(        LayoutInflater.from(parent.context).inflate(R.layout.item_quiz_type, parent, false)    )    override fun onBindViewHolder(holder: QuizAdapter.ViewHolder, position: Int) {        val questionsTypeModel = questionsList[position]        holder.bind(questionsTypeModel)    }    override fun getItemCount(): Int = questionsList.size    inner class ViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        val image = itemView.findViewById<ImageView>(R.id.itemImage)        val questionsType = itemView.findViewById<TextView>(R.id.questionsName)        fun bind(questionsTypeModel: QuestionsTypeModel) {            image.setImageResource(questionsTypeModel.imageId)            questionsType.text = questionsTypeModel.typeName            itemView.setOnClickListener {                Questions.index = questionsTypeModel.questionsId                onItemClick.invoke(questionsTypeModel)            }        }//bind    }}