package com.example.studentapp.databaseimport android.content.Contextimport androidx.room.Databaseimport androidx.room.Roomimport androidx.room.RoomDatabase@Database(entities = [MaterialsData::class], version = 1)abstract class MaterialDatabase : RoomDatabase() {    abstract fun materialDao(): MaterialsDao    companion object {        private var INSTANCE: MaterialDatabase? = null        fun getDatabase(context: Context): MaterialDatabase {            val tempInstance = INSTANCE            if (tempInstance != null) {                return tempInstance            }            synchronized(this) {                val instance = Room.databaseBuilder(                    context.applicationContext,                    MaterialDatabase::class.java,                    "material_database"                ).build()                INSTANCE = instance                return instance            }        }    }}