package com.example.studentapp.databaseimport android.provider.ContactsContractimport androidx.room.Daoimport androidx.room.Deleteimport androidx.room.Insertimport androidx.room.Query@Daointerface UserDao {    @Query("SELECT * FROM users;")    fun getAll(): List<UserInfo>    @Query("SELECT NOT EXISTS(SELECT * FROM users WHERE email = :email )")    fun isNotExists(email:String?):Boolean    @Insert    fun insertData(arts: UserInfo?)    @Delete    fun delete(art: UserInfo)}