package com.example.studentapp.database.questionsdbimport androidx.room.Daoimport androidx.room.Deleteimport androidx.room.Insertimport androidx.room.Queryimport com.example.studentapp.models.Questions@Daointerface QuestionsDao {    @Query("SELECT * FROM questions;")    fun getAll(): List<Questions>    @Query("SELECT NOT EXISTS(SELECT * FROM questions WHERE question = :question )")    fun isNotExists(question:String?):Boolean    @Query("SELECT * FROM questions WHERE questionsId = :questionsId")    fun getItemAnswer(questionsId:String):List<Questions>    @Insert    fun insertData(arts: QuestionsData?)    @Delete    fun delete(art: QuestionsData)}