package com.example.studentapp.database.topUsersDatabaseimport androidx.room.Daoimport androidx.room.Deleteimport androidx.room.Insertimport androidx.room.Query@Daointerface TopUserDao {    @Query("SELECT * FROM topUser;")    fun getAll(): List<TopUserData>    @Insert    fun insertAll(vararg arts: TopUserData?)    @Query("SELECT NOT EXISTS(SELECT * FROM topUser WHERE email = :email)")    fun isNotExists(email:String?):Boolean    @Delete()    fun delete(vararg art: TopUserData)}