package com.example.studentapp.database.topUsersDatabaseimport android.content.Contextimport androidx.room.Databaseimport androidx.room.Roomimport androidx.room.RoomDatabase@Database(entities = [TopUserData::class], version = 1)abstract class TopUserDatabase : RoomDatabase() {    abstract fun topUserDao() : TopUserDao    companion object {        private var INSTANCE: TopUserDatabase? = null        fun getDatabase(context: Context): TopUserDatabase {            val tempInstance = INSTANCE            if (tempInstance != null) {                return tempInstance            }            synchronized(this) {                val instance = Room.databaseBuilder(                    context.applicationContext,                    TopUserDatabase::class.java,                    "toppers_database"                ).build()                INSTANCE = instance                return instance            }        }    }}